/*
 * Główny plik dla stylów.
 * Wszystkie partials podłączamy tutaj.
 */

@import './base/button';
@import './base/container';
@import './base/section';


// @import './components/customer-reviews';
@import './components/gallery';
@import './components/hero';
@import './components/mobile-menu';
@import './components/mobile';
@import './components/modal-menu';
@import './components/product-card';
@import './components/section-3';
@import './components/section4';


@import './layout/footer';
@import './layout/grid';
@import './layout/header';


@import './utils/variables';

sources.sass\\base\\_button.scss:
@import '../utils/_variables.scss';

.product-card__btn {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    margin-right: auto;
    margin-left: auto;
    margin-top: 40px;
    border: 0px;
    border-radius: 50%;
    width: 40px;
    height: 40px;
    background-color: getColor('text-description');
    background-image: url("../images/arrow-right.svg");
    transition-property: margin-top, box-shadow;
    transition-duration: 500ms;

    transition-property: margin-top, box-shadow 500ms, transform 500ms ease-in-out 500ms;

    &:hover {
        cursor: pointer;
        box-shadow: 8px 8px 6px 1px rgba(0, 0, 0, 0.3);
        transform: translateY(1);
        transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
        margin-top: 35px;
    }
}

