sources.sass\\components\\_hero.scss:
@import '../utils/variables';

.icecream {
    background-color: getColor('text-main');
    border-radius: 50%;
    border-color: getColor('text-main');
    width: 538px;
    height: 538px;
}

.hero {
    display: flex;
    flex-wrap: wrap;

    &__title {
        display: block;
        font-family: getFont('main-font');
        letter-spacing: 0.04em;
        text-transform: uppercase;
        color: getColor('text-description');
        margin: 0;
        z-index: 4;
    }

    &__links {
        display: flex;
        flex-direction: row;
        z-index: 4;
}

        &__link {
            text-decoration: none;
            z-index: 4;
        }

        &__img {
            position: absolute;
        }

        &__ice {
            background-color: getColor('text-main');
            border-radius: 50%;
            border-color: getColor('text-main');
            z-index: 0;
        }

    .button__title--white {
        color: getColor('text-description');
}

    .button__title--red {
        color: getColor('text-category');

    }



}

@media screen and (max-width: 767px){
.hero {
    
    &__header {
        padding-top: 137px;
        z-index: 4;
    }

    &__title {

        max-width: 218px;
        font-size: 26px;
        line-height: 1.1538;
                        
        &--red{
            font-size: 48px;
        }
    }

    &__links {
        gap: 9px;
    }


    &__ice {
    width: 346px;
    height: 344px;
    margin-left: 36px;
    margin-top: -280px;
    margin-bottom: 100px;
    margin-right: -100px;
    }

&__lokals,
&__eco{
    display: none;
}
}

.button__header {
    display: none;
}

.button {

    &--mobile,
    &--white,
    &--red {
        text-decoration: none;
        display: flex;
        align-items: center;
        justify-content: center;
        gap: 12px;
        border-style: none;
        z-index: 4;
        cursor: pointer;
    }

    &--mobile {
        border-radius: 22px;
        width: 209px;
        height: 44px;
        align-items: center;
        margin: 0 auto;

    }

    &--mobile,
    &--white {
        background-color: getColor('text-description');
        border-color: getColor('text-description');
    }

    &--mobile:hover,
    &--mobile:focus,
    &--white:hover,
    &--white:focus {
        background: #ECEBEB;
        border-color: #ECEBEB;
        fill: getColor('text-category');
    }

    &--red {
        background: getColor('text-category');
        border-color: getColor('text-category');

        &:hover,
        &:focus {
            background: #C7133E;
            border-color: #C7133E;
        }
    }

    &--white,
    &--red {
        border-radius: 20px;
        height: 24px;
    }
}

.button__title--header,
.button__title--white,
.button__title--red {
    font-family: getFont('secondary-font');
    line-height: 1.3125;
    display: flex;
    align-items: center;
    text-align: center;
    text-decoration: none;
    font-size: 8px;
}




.button__title--header {
    font-weight: 700;
    color: getColor('text-category');
    font-size: 16px;
}

.icecream {
    width: 171px;
    height: 410px;
    left: 117px;
    top: 92px;
}

.girl,
.milk {
    display: none;
}

}



@media screen and (min-width:768px) and (max-width:1199px){
.hero {

    &__header {

        padding-top: 15px;
    }

    &__title {

        max-width: 162px;
        font-size: 22px;
        line-height: 1.16;

        &--red {
            font-size: 34px;
        }
    }

    &__links {
        gap: 9px;
    }
         
    &__ice {
        width: 324px;
        height: 322px;
        margin-left: 110px;
        margin-top: -50px;
    }

    &__lokals {
        gap: 18px;
        display: flex;
        flex-direction: column;
        width: 45px;
        padding-left: 60px;
    }
                                    
    &__lokal {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        align-content: center;
    }
                
    &__box--next,
    &__box {
        background-color: getColor('text-description');
        border-radius: 50%;
        width: 16px;
        height: 16px;
        margin-left: 60px;
        margin-bottom: 12px;
