@import '../utils/variables';

.mobile {
    position: fixed;
    right: 0;
    top: 0;
    width: 250px;
    min-height: 502px;
    background-color:transparent;
    transition: opacity 250ms;
    z-index: 7;
}

.mobile__modal {
    position: absolute;
    right: 0px;
        top: 50%;
    transform: translate(-50%, -50%);
    width: 250px;
    min-height: 502px;
    background-color: getColor('mobileColor') ;

}

.mobile__close{
    width: 14px;
    height: 14px;
    position: absolute;
    top: 23px;
    right: 34px;
    border: none;
    background-color: transparent;
    cursor: pointer;
}

.close-btn-icon {
    width: 14px;
    height: 14px;
    position: absolute;
    top: 0px;
    right: 0px;
    border: none;
    background-color: transparent;
    cursor: pointer;

    &:hover,
    &:focus{
    transform: rotate(-90deg) 3000ms;
    }
}

.is-hidden {
    opacity: 0;
    visibility: hidden;
    pointer-events: none;

}

.mobile{
    &__btn-icon {
            width: 4px;
            height: 8px;
        }
    &__menu{
        display: flex;
        justify-content: center;
        margin:0 auto;
        align-items: flex-start;
        align-content: center;
        flex-direction: column;
        padding-top: 60px;

    }

    &__links{
        list-style: none;
    }

    &__link{
        font-family: getFont('secondary-font');
        font-weight: 700;
        font-size: 14px;
        line-height: 18px;
        color: getColor('text-description');
    }

    &__links{
        list-style: none;
        display: flex;
        flex-direction: column;
        margin-top: 19px;
        padding-left: 0;
    }

    &__item{
        border-top: 1px solid getColor('mobileColor');
        border-bottom: 1px solid getColor('mobileColor');
        padding: 21px 13px;

        
    }

    &__link{
    text-decoration: none;
    font-family: getFont('secondary-font');
    font-weight: 700;
    font-size: 14px;
    line-height: 1.2857;
    color:getColor('text-description');
    
    &:hover,
    &:focus{
        color: getColor('text-category');
    }
}
}


@media screen and (min-width:767px){
    .mobile__modal {
            right: -70px;
            top: 50%;
            width: 250px;
            min-height: 502px;
    }

    .mobile {
        width: 254px;
        min-height: 1020px;

    }

    .mobile__modal {
        width: 254px;
        min-height: 1020px;
        }

    .mobile__item{
        padding-left:24px;
    }
}
@media screen and (min-width:1200px) {
    .mobile,
    .mobile__modal{
        display: none;
    }
}

sources.sass\\components\\_modal-menu.scss:
@import '../utils/_variables.scss';

.backdrop{
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.2);
    transition: 
    transform 1000ms, cubic-bezier(0.175, 0.885, 0.32, 1.275),
    opacity 1000ms cubic-bezier(0.175, 0.885, 0.32, 1.275),
    visibility 1000ms cubic-bezier(0.23, 1, 0.320, 1);
}

.is-hidden{
    opacity: 0;
    visibility: hidden;
    pointer-events: none;
}
.modal-navigation__button--open{
    background-color: none;
    fill: getColor('text-category');
}

.modal-navigation{
    width: 250px;
    height: 502px;
    position: absolute;
    background-color: getColor('background-modal');
    top: 0;
    right: 0;

    @media screen and (min-width: 768px) {
        width: 254px;
        height: 1020px;
      } 
  }


.modal-navigation__button--close {
    position: absolute;
    top: 8px;
    right: 8px;
    fill:getColor('text-description');
    background-color:getColor('background-modal');
    margin-left: auto;
    cursor: pointer;
    border: none;
}

.modal-navigation__button--buy{
    background-color: getColor('text-description');
    color: getColor('text-category');
    font-family:$family-secondary ;
    font-weight: 700;
    font-size: 16px;
    line-height: 1,31;
    border-radius: 22px;
    border: none;
    cursor: pointer;
    text-align: center;
    width: 209px;
    height: 44px;
    display:inline-flex;
    align-items: center;
    justify-content: space-around;
    margin-top:60px; 
    margin-bottom: 19px;
    display: block;
    margin-left: auto;
    margin-right: auto;
}

.modal-navigation__icon--buy{
    fill:getColor('text-category');
}

.modal-navigation__list{
    padding-left: 21px;

    @media screen and (min-width: 768px) {
        padding-left: 24px;
      } 
}

.modal-navigation__item{
    font-family:$family-secondary ;
    font-weight: 700;
    font-size: 14px;
    line-height: 1.29;
    color: getColor('text-description');
    list-style: none;
    text-decoration: none;
    border-bottom:1px solid getColor('background-modal');
    border-top:1px solid getColor('background-modal');
    height: 42px;
    transition: color 250ms cubic-bezier(0.4, 0, 0.2, 1);
  
    &:focus,
    &:hover {
      color:getColor('text-category');
    }
  }

sources.sass\\utils\\_variables.scss:
// ===colors===

$colors: (
  'text-main': #ffa5ba,
  'text-category': #d41443,
  'text-description': #ffffff,
  'text-info': #907e82,
  'background-first': #ffb8ca,
  'background-second': #f0d1a5,
  'background-third': #c2e297,
  'background-social': #fea5bb,
  'mobileColor': #E17992,
);



@function getColor($key) {
  @return map-get($colors, $key);
}

// ===/colors===

// ===fonts===

$family-main: 'Titan One', cursive;
$family-secondary: 'DM Sans', sans-serif;
$fonts: (
  'main-font': $family-main,
  'secondary-font': $family-secondary,
);

@function getFont($key) {
    @return map-get($fonts, $key);
}

// ===/fonts===

